 // hide/show top-menu
 $(document).ready(function(){

    $('.navbar-toggle').click(function(){
      $('#bs-example-navbar-collapse-1').toggle();
     
     });
   $(window).resize(function() {
  if($(window).width() >= 768) { 

    $('#bs-example-navbar-collapse-1').css('display','block');
  } });
   $(window).resize(function() {  
    if($(window).width() < 768) { 

      $('#bs-example-navbar-collapse-1').css('display','none');
  } });

});



/*--------- dropdown menu ------*/
$(document).ready(function(){

  $("#menu-item-206>a").removeAttr("href");

  $('#menu-item-206>a').click(function(event){
   event.stopPropagation();
    if ($('#menu-item-206 ul').css('display')== 'block'){
      $('#menu-item-206 ul').hide();
    }
    else
      $('#menu-item-206 ul').show();
   });


  $(window).click(function() {
    if ($('#menu-item-206 ul').css('display')== 'block'){
      $('#menu-item-206 ul').hide();
    }
  });

  $('#menu-item-206 ul').click(function(event){
    event.stopPropagation();
  });

});
/* -------- clicked menus styles --------*/
 $(document).ready(function(){
    $( ".menu-item a" ).each(function() {
      if( $(this).attr("href") == window.location.href){
        $(this).css({"font-weight":"600", "background-color": "rgba(255, 255, 255, 0.2)","text-decoration":"none"});    
      }
    });
    $( ".footer-menus a" ).each(function() {
      if( $(this).attr("href") == window.location.href){
        $(this).css({"color":"#636363"});    
      }
    });
  });

$(document).ready(function(){
    $( ".current-cat a" ).each(function() {
      if( $(this).attr("href") == window.location.href){
        $(this).css({"background-color":"#f1f1f1","text-decoration":"none"});    
      }
    });
  });

$(document).ready(function(){
  $('#file-upload').on('click',function(){
    $('#upload-file').click();

  });
    $('#upload-file').change(function() {
    var files = $(this)[0].files;
    for (var i = 0; i < files.length; i++) {
        $('.file-text').append(files[i].name + '<br/>');
    }
});
});

