/* Hook scroll*/
$("#up").click(function(){
        $('html, body').animate({
         scrollTop: $('#down').offset().top }, 'slow');
 })

$("#menu-item-364").click(function() {
    $('html, body').animate({
        scrollTop: $("#section_5").offset().top
    }, 2000);
});